import React from "react"
import {View ,Text, Image, TouchableOpacity,TextInput, StyleSheet,} from  "react-native";


export default function Login({navigation}){ 

    return (
        <Button      title="Go to OnBoardScreen"      
        onPress={() =>        
            navigation.navigate('OnBoardScreen', { name: 'Screen' })      
        }    />  );};
        const OnBoardScreen = ({ navigation, route }) => {  

            return <Text>This is {route.params.name}'s OnBoardScreen</Text>;
        };

    <View style={{flex: 1, backgroundColor: "#d49f7b", alignItems:"center",justifyContent:"center"}}>

        <Image 
        style={{
                width: 200,
                height: 200,
                borderRadius:20,
                marginBottom:50,
                transform: [{rotate:"45deg"}]
            }}
        source ={{
            uri: " https://nigerianbuildingdesigns.com/wp-content/uploads/2020/02/7-BEDROOM-DUPLEX-WITH-PENT-FLOOR-1-1.jpg"

        }}
        />

        <Text style={{color: "rgb(0,0,0,0.8)", fontSize:30}}>Welcome to</Text>
        <Text style={{color: "black", fontSize: 29,fontWeight: '600'}}>PHOENIX-HOMES</Text>
       <TouchableOpacity >
    <TextInput  
        style={{
            backgroundColor:"white", 
           backgroundColor:"#e3e3e3", 
            borderRadius:10,
            flexDirection: "row",
            alignItems: "center",
           padding:10,
           paddingHorizontal:40,
           marginTop: 20,
        }}

         placeholder= 'Username'  />
         </TouchableOpacity>
       <TouchableOpacity> 
    <TextInput
  style={{
    backgroundColor:"#203931",
    borderRadius:10, 
    flexDirection:"row",
    alignItems:"center",
   padding:10,
   paddingHorizontal:40,
   marginTop: 20,
}}


         placeholder='Password' />

        </TouchableOpacity>

        <TouchableOpacity>  
        <Text style={{marginTop:10, fontWeight:"500", color:"grey"}} > LOGIN </Text>

        </TouchableOpacity>
             <TouchableOpacity>
        <Text style={{marginTop:10, fontWeight:"500", color:"grey"}} >
            Not A Member?
            <Text style={{color:"orange", fontWeight:"bold",}}> SignUp</Text>
        </Text>
        </TouchableOpacity>
 </View>







   const styles = StyleSheet.create({
       input: {
        borderWidth:1,
        borderColor: "black",
        padding:8,
        margin:10,
        width:200,
        borderRadius:10
       }
});
